var dateformat = require('dateformat');

// Miscellanous template helpers

exports.today = function(format) {
  return dateformat(new Date(), format);
};

exports.stripBanner = function(src) {
  return src.replace(/^\s*\/\*[^!][\s\S]*?\*\/\s*/, '');
};

